import { iso3166Countries } from "./internal/iso3166Countries";
export function isValidIso3166Code(code) {
    return typeof code === "string"
        ? code.length === 2
            ? iso3166Countries.some(function (x) { return x.alpha2Code === code; })
            : code.length === 3
                ? iso3166Countries.some(function (x) { return x.alpha3Code === code; })
                : false
        : (code > 0 && code < 1000)
            ? iso3166Countries.some(function (x) { return x.numericCode === code; })
            : false;
}
