import { iso4217Currencies } from "./internal/iso4217Currencies";
export function isValidIso4217Code(code) {
    return typeof code === "string"
        ? code.length === 3
            ? iso4217Currencies.some(function (x) { return x.alpha3Code === code; })
            : false
        : (code > 0 && code < 1000)
            ? iso4217Currencies.some(function (x) { return x.numericCode === code; })
            : false;
}
