export function roundUsingHalfAwayFromZero(value, precision) {
    if (precision === Number.POSITIVE_INFINITY) {
        return value;
    }
    if (precision < 0 || precision >= 16 || isNaN(precision)) {
        throw new RangeError("precision value is out of range.");
    }
    var negativeNumber = value < 0;
    if (negativeNumber) {
        value = Math.abs(value);
    }
    var power = Math.pow(10, Math.trunc(precision)), result = Math.round((value * power).toPrecision(15)) / power;
    return negativeNumber ? -result : result;
}
